<?php\nfunction displayFirstImage($folderPath, $defaultImagePath) {\n    // Check if the folder exists\n    if (!is_dir($folderPath)) {\n        echo '<img src="' . htmlspecialchars($defaultImagePath) . '" alt="Default Image">';\n        return;\n    }\n\n    // Open the directory\n    if ($handle = opendir($folderPath)) {\n        // Loop through the files in the directory\n        while (false !== ($entry = readdir($handle))) {\n            // Skip directories and non-JPG files\n            if ($entry != "." && $entry != ".." && pathinfo($entry, PATHINFO_EXTENSION) === 'jpg') {\n                echo '<img src="' . htmlspecialchars($folderPath . '/' . $entry) . '" alt="First Image">';\n                closedir($handle);\n                return;\n            }\n        }\n        closedir($handle);\n    }\n\n    // If no image was found, display the default one\n    echo '<img src="' . htmlspecialchars($defaultImagePath) . '" alt="Default Image">';\n}\n\n// Usage example, assuming $folderLocation is your custom field parameter\n$folderLocation = '/path/to/your/folder/';\n$defaultImagePath = '/path/to/default/image.jpg';\n\ndisplayFirstImage($folderLocation, $defaultImagePath);\n?>