<?php\n$file = '/path/to/file.txt';\n\n// Open the file for writing\n$handle = fopen($file, 'a');\n\nif (!$handle) {\n    die('Could not open file!');\n}\n\n// Attempt to get an exclusive lock\n$gotLock = flock($handle, LOCK_EX | LOCK_NB);\n\nif ($gotLock) {\n    // Write your less important data if the lock is obtained\n    fwrite($handle, "Less important data\n");\n    \n    // Release the lock\n    flock($handle, LOCK_UN);\n} else {\n    // If the lock could not be obtained, write your more important data without waiting\n    file_put_contents($file, "More important data\n", FILE_APPEND);\n}\n\nfclose($handle);\n?>