<?php foreach ($products as $product): ?>\n    <?php $form = new AddToCartForm(array('productId' => $product->getId())); ?>\n    <div>\n        <h3><?php echo $product->getName(); ?></h3>\n        <p><?php echo $product->getDescription(); ?></p>\n        \n        <!-- Render form widget and button -->\n        <form action="/cart/add" method="post">\n            <?php echo $form['productId']->render() ?>\n            <?php echo $form['quantity']->renderLabel('Add [' . $form['quantity']->getValue() . '] of these to your shopping cart') ?>\n            <?php echo $form['quantity']->render() ?>\n            <button type="submit">Go</button>\n        </form>\n    </div>\n<?php endforeach; ?>