$db = Zend_Registry::get('db');\n$username = 'exampleUsername'; // Assume this is the user input or some variable\n\n// Use a placeholder in the SQL statement\n$select = $db->select()\n             ->from(array('m' => 'message'), array('num' => new Zend_Db_Expr('COUNT(*)')))\n             ->where($db->quoteInto("m.message LIKE ?", $username . ' %'));\n\n$row = $db->fetchRow($select);