<?php\n$url = "http://example.com"; // Replace with your target URL\n$targetString = "maybe baby love you";\n\n// Fetch the contents of the URL\n$pageContent = @file_get_contents($url);\n\nif ($pageContent === FALSE) {\n    die("Could not retrieve the page content.");\n}\n\n// Search for the target string\nif (strpos($pageContent, $targetString) === false) {\n    // The string was not found\n    echo "The string '$targetString' was not found in the URL.";\n    // Do action X here\n} else {\n    // The string was found\n    echo "The string '$targetString' was found in the URL.";\n}\n?>