amespace CRM\Plugins;\n\nclass PluginManager {\n    private static $plugins = [];\n\n    public static function register($pluginClassName, $config) {\n        self::$plugins[$pluginClassName] = new $pluginClassName($config);\n    }\n\n    public static function loadAll() {\n        foreach (glob(__DIR__ . '/*/config.php') as $filename) {\n            require $filename;\n        }\n    }\n\n    public static function getPluginsList(): array {\n        return self::$plugins;\n    }\n}