// Prepare and bind the first query\n$stmt1 = $connection->prepare("SELECT name FROM clients WHERE sector = ?");\n$stmt1->bind_param("s", $sectorlink);\n$stmt1->execute();\n$result1 = $stmt1->get_result();\n\n$names = [];\nwhile ($row = $result1->fetch_assoc()) {\n    $names[] = $row['name'];\n}\n\nif (!empty($names)) {\n    // Create a string of placeholders for the `IN` clause\n    $placeholders = implode(',', array_fill(0, count($names), '?'));\n    \n    // Prepare and bind the second query\n    $stmt2 = $connection->prepare("SELECT count(*) FROM studies WHERE client IN ($placeholders) ORDER BY date DESC");\n    $types = str_repeat('s', count($names));\n    $stmt2->bind_param($types, ...$names);\n    $stmt2->execute();\n    $result2 = $stmt2->get_result();\n\n    while ($row = $result2->fetch_assoc()) {\n        // Process your result as needed\n    }\n} else {\n    echo "No names found for the given sector.";\n}