<?php\n// Load URL helper if not already loaded\n$this->load->helper('url');\n\n$current_segment = $this->uri->segment(1);\n\n// Example usage for creating a link conditionally based on the current segment\nfunction conditional_link($segment, $link_text, $link_url) {\n    $ci =& get_instance();\n    $current_segment = $ci->uri->segment(1);\n\n    if ($segment != $current_segment) {\n        echo anchor($link_url, $link_text);\n    } else {\n        echo $link_text;\n    }\n}\n\n// Usage of the function\nconditional_link('about', 'About Us', 'about');\nconditional_link('contact', 'Contact', 'contact');\n?>