if (isset($_POST["song"]) && $_POST['song'] != "") {\n    $song = $_POST["song"];\n} else {\n    $song = array();\n}\n\n$dom = new DOMDocument("1.0");\n\n// create root element\n$root = $dom->createElement("playlist");\n$dom->appendChild($root);\n$root->setAttribute('version', "1");\n$root->setAttribute('xmlns', "http://xspf.org/ns/0/");\n$rootnext = $dom->createElement("trackList");\n$root->appendChild($rootnext);\n\nforeach ($song as $counter) {\n    $tokens = ",";\n    $tokenized = strtok($counter, $tokens);\n    \n    // create child element\n    $songElement = $dom->createElement("track");\n    $rootnext->appendChild($songElement);\n    \n    $creator = $dom->createElement("creator", "www.musicking.in");\n    $songElement->appendChild($creator);\n\n    $title = $dom->createElement("title", $tokenized);\n    $songElement->appendChild($title);\n\n    $tokenized = strtok($tokens);\n    $location = $dom->createElement("location", $tokenized);\n    $songElement->appendChild($location);\n}\n\n$xmlContent = $dom->saveXML();\n\n// Database connection\n$connection = new mysqli('localhost', 'username', 'password', 'database_name');\nif ($connection->connect_error) {\n    die("Connection failed: " . $connection->connect_error);\n}\n\n$stmt = $connection->prepare("INSERT INTO playlist_table (xml_data) VALUES (?)");\n$stmt->bind_param("s", $xmlContent);\n$stmt->execute();\n$stmt->close();\n$connection->close();