class PrintCommand {\n       private $text;\n\n       public function __construct($text) {\n           $this->text = $text;\n       }\n\n       public function __invoke() {\n           echo $this->text . PHP_EOL;\n       }\n   }\n\n   $command = new PrintCommand('Executing command...');\n   $command(); // Outputs: Executing command...