class Something {\n    // Constructor method\n    function __construct() {\n        // Code here will run automatically when an object is created\n        $this->automaticxxx_something_which_runs_when_class_is_created();\n    }\n\n    // Method that will be called in the constructor\n    private function automaticxxx_something_which_runs_when_class_is_created() {\n        echo "This runs automatically!";\n    }\n}\n\n$s = new Something();