<?php\nif (isset($_POST['file'])) {\n    $filePath = '/pdf/' . basename($_POST['file']);\n    if (file_exists($filePath)) {\n        header('Content-Description: File Transfer');\n        header('Content-Type: application/pdf');\n        header('Content-Disposition: attachment; filename="' . basename($filePath) . '"');\n        readfile($filePath);\n        exit;\n    } else {\n        echo "File not found.";\n    }\n}\n?>