function getHighlightedSnippet($text, $searchTerms) {\n    // Convert search terms into an array if it's not already\n    $searchTermsArray = !is_array($searchTerms) ? explode(' ', $searchTerms) : $searchTerms;\n    \n    // Create a regex pattern from the search terms\n    $pattern = '/\b('. implode('|', array_map(function ($word) {\n        return preg_quote($word, '/');\n    }, $searchTermsArray)) . ')\b/i';\n    \n    // Extract snippet (assuming $text is the text of the document)\n    $snippetLength = 150; // Total length of snippet\n    if (preg_match_all($pattern, $text, $matches, PREG_OFFSET_CAPTURE)) {\n        foreach ($matches[0] as $match) {\n            $offset = $match[1];\n            // Calculate start and end positions for the snippet\n            $startPos = max(0, $offset - floor($snippetLength / 2));\n            $endPos = min(strlen($text), $startPos + $snippetLength);\n            \n            $snippet = substr($text, $startPos, $endPos - $startPos);\n            \n            // Highlight search terms in the snippet\n            $highlightedSnippet = preg_replace_callback($pattern, function ($match) {\n                return '<strong>' . htmlspecialchars($match[0]) . '</strong>';\n            }, $snippet);\n            \n            return '...' . $highlightedSnippet . '...';\n        }\n    }\n    \n    // If no search terms are found in the text, return an ellipsis or nothing\n    return '';\n}\n\n// Example usage:\n$text = "This is a sample text where we want to find highlight sample words.";\n$searchTerms = "sample words";\necho getHighlightedSnippet($text, $searchTerms);