<?php\nrequire_once 'functions.php';\n\n// Get the requested language from the URL parameter, default to English if not set.\n$requestedLang = isset($_GET['lang']) ? $_GET['lang'] : 'en';\n\ntry {\n    $languageStrings = load_language($requestedLang);\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n    exit;\n}\n\n// Example usage of translated strings\necho '<p>' . htmlspecialchars($languageStrings['first_name']) . '</p>';\necho '<p>' . htmlspecialchars($languageStrings['last_name']) . '</p>';