<?php\n\nfunction FilterALLHTML($var) {\n    if ($var !== null) { // Use strict comparison for NULL check\n        $text = strip_tags($var);\n        $search = array("/f.?u.?c.?k/i", "/(s|$).?h.?i.?t/i", '/(potspace|mycrib|palbolt|pot space)/i');\n        $text = preg_replace($search, '', $text);\n        return $text;\n    }\n    return null;\n}\n\n// Array to hold the variables\n$fields = array('city', 'state', 'title', 'division');\n\n// Loop through each field and filter it\nforeach ($fields as &$field) {\n    if (isset($$field)) { // Check if variable exists\n        $$field = FilterALLHTML($$field);\n    }\n}\n\n?>