// For CakePHP 1.2\nfunction sendErrorEmail() {\n    App::uses('EmailComponent', 'Controller/Component');\n    \n    $email = new EmailComponent(new ComponentCollection());\n    \n    // Configure email settings\n    $email->delivery   = 'smtp';\n    $email->template   = 'default';\n    $email->sendAs     = 'text';\n    $email->from       = 'your-email@example.com';\n    $email->to         = 'recipient-email@example.com';\n    $email->subject    = 'Error';\n    \n    // Send the email\n    return $email->send();\n}