$results = [\n    ['timestamp' => 1609459200, 'message' => 'Article added', 'articleNum' => 1],\n    ['timestamp' => 1609545600, 'message' => 'Article updated', 'articleNum' => 1],\n    ['timestamp' => 1610150400, 'message' => 'Article updated', 'articleNum' => 1],\n    // More data...\n];\n\n$articles = [];\n\nforeach ($results as $row) {\n    if (strpos($row['message'], 'added') !== false) {\n        // This is a creation entry\n        $articles[$row['articleNum']]['created'] = $row['timestamp'];\n    } elseif (strpos($row['message'], 'updated') !== false) {\n        // This is an update entry, set only if it's the latest one\n        if (!isset($articles[$row['articleNum']]['modified']) || $row['timestamp'] > $articles[$row['articleNum']]['modified']) {\n            $articles[$row['articleNum']]['modified'] = $row['timestamp'];\n        }\n    }\n}\n\n// Output result\necho "<pre>";\nprint_r($articles);\necho "</pre>";