function intOverflowAdd($a, $b) {\n    // Using BCMath (arbitrary precision mathematics)\n    return (int)(bcmod(bcadd((string)$a, (string)$b), "2147483648"));\n}\n\nfunction intOverflowXor($a, $b) {\n    // Ensure values are within 32-bit unsigned\n    $a = ($a & 0xFFFFFFFF);\n    $b = ($b & 0xFFFFFFFF);\n    return ($a ^ $b);\n}\n\nfunction f($x1, $x2, $x3, $x4) {\n    $sum = intOverflowAdd($x1, $x2);\n    $xorResult = intOverflowXor($sum, $x3);\n    return (int)(intval(bcmod(bcadd((string)$xorResult, (string)$x4), "2147483648")));\n}\n\n$x1 = -1580033017;\n$x2 = -2072974554;\n$x3 = -1170476976;\n$x4 = -1007518822;\n\necho f($x1, $x2, $x3, $x4); // This should output 1617621783 as in C#