<?php\n\n// DB connection details for the source database (Joomla installation)\n$sourceHost = 'localhost';\n$sourceUsername = 'your_source_db_user';\n$sourcePassword = 'your_source_db_password';\n$sourceDatabase = 'your_source_db_name';\n\n// DB connection details for the target database\n$targetHost = 'localhost';\n$targetUsername = 'your_target_db_user';\n$targetPassword = 'your_target_db_password';\n$targetDatabase = 'your_target_db_name';\n\n// Connect to source and target databases\n$sourceConn = new mysqli($sourceHost, $sourceUsername, $sourcePassword, $sourceDatabase);\n$targetConn = new mysqli($targetHost, $targetUsername, $targetPassword, $targetDatabase);\n\nif ($sourceConn->connect_error || $targetConn->connect_error) {\n    die('Connection error: ' . ($sourceConn->connect_error ?: $targetConn->connect_error));\n}\n\n// Fetch table structure from source database\n$tablesQuery = "SHOW TABLES";\n$result = $sourceConn->query($tablesQuery);\n\nif ($result !== false) {\n    while ($row = $result->fetch_row()) {\n        $tableName = $row[0];\n        \n        // Get the CREATE TABLE statement for each table\n        $createTableQuery = "SHOW CREATE TABLE `$tableName`";\n        $createResult = $sourceConn->query($createTableQuery);\n\n        if ($createResult !== false) {\n            $createTableData = $createResult->fetch_row();\n            $createStatement = $createTableData[1];\n\n            // Execute the CREATE TABLE statement on the target database\n            if (!$targetConn->query("DROP TABLE IF EXISTS `$tableName`")) {\n                echo "Error dropping table: " . $targetConn->error . "<br>";\n            }\n            \n            if ($targetConn->query($createStatement)) {\n                echo "Table '$tableName' created successfully.<br>";\n            } else {\n                echo "Error creating table: " . $targetConn->error . "<br>";\n            }\n\n            $createResult->close();\n        } else {\n            echo "Error retrieving table structure for '$tableName': " . $sourceConn->error . "<br>";\n        }\n    }\n} else {\n    echo "Error fetching tables: " . $sourceConn->error;\n}\n\n// Close connections\n$sourceConn->close();\n$targetConn->close();\n\n?>