$data = mysql_query("\nSELECT tblpm.* \nFROM tblpm\nINNER JOIN (\n    SELECT thread_id, MAX(date_sent) AS max_date_sent\n    FROM tblpm\n    WHERE receiver_id = '$usrID'\n    GROUP BY thread_id\n) AS latest_messages ON tblpm.thread_id = latest_messages.thread_id AND tblpm.date_sent = latest_messages.max_date_sent\nWHERE tblpm.receiver_id = '$usrID'\nORDER BY tblpm.date_sent DESC") or die(mysql_error());