// application/controllers/Plugins/Sidebar.php\n   class Application_Controller_Plugin_Sidebar extends Zend_Controller_Plugin_Abstract\n   {\n       public function dispatchLoopShutdown()\n       {\n           $frontController = Zend_Controller_Front::getInstance();\n           $actionHelperBroker = Zend_Controller_Action_HelperBroker::getStaticHelper('ActionStack');\n           \n           // Push a request for the sidebar action\n           $request = new Zend_Controller_Request_Http();\n           $request->setModuleName('default')\n                   ->setControllerName('sidebar')\n                   ->setActionName('index')\n                   ->setDispatched(false);\n                   \n           $actionHelperBroker->pushStack($request);\n       }\n   }