function parseFeed($raw_xml, $feed_type) {\n    $xml = simplexml_load_string($raw_xml);\n    \n    if ($xml === false) {\n        echo "Failed loading XML: ";\n        foreach(libxml_get_errors() as $error) {\n            echo "$error\n";\n        }\n        return;\n    }\n\n    switch ($feed_type) {\n        case 'channel':\n            foreach ($xml->channel as $channel) {\n                // Process each channel\n                echo 'Channel Title: ' . (string)$channel->title . "\n";\n                // Add your logic here\n            }\n            break;\n        case 'item':\n            foreach ($xml->item as $item) {\n                // Process each item\n                echo 'Item Title: ' . (string)$item->title . "\n";\n                // Add your logic here\n            }\n            break;\n        default:\n            echo "Unknown feed type\n";\n    }\n}\n\n// Usage\n$raw_xml = file_get_contents('feed1.xml');\n$raw_xml2 = file_get_contents('feed2.xml');\n\nparseFeed($raw_xml, 'channel');\nparseFeed($raw_xml2, 'item');