function updateDemo($demoTitle, $desc, $keyword, $uploadedFile, $clientname, $uploadedImage, $adminName, $demoID) {\n    // Assuming you have a PDO instance named $pdo\n    try {\n        $sql = "UPDATE demos SET dmTitle = :dmTitle, dmDesc = :dmDesc, dmKey = :dmKey, dmLink = :dmLink,\n                                client = :client, imageTitle = :imageTitle, userName = :userName WHERE id = :id";\n\n        $stmt = $pdo->prepare($sql);\n        $stmt->bindParam(':dmTitle', $demoTitle);\n        $stmt->bindParam(':dmDesc', $desc);\n        $stmt->bindParam(':dmKey', $keyword);\n        $stmt->bindParam(':dmLink', $uploadedFile);\n        $stmt->bindParam(':client', $clientname);\n        $stmt->bindParam(':imageTitle', $uploadedImage);\n        $stmt->bindParam(':userName', $adminName);\n        $stmt->bindParam(':id', $demoID, PDO::PARAM_INT);\n\n        $result = $stmt->execute();\n\n        if ($result) {\n            return 'yes';\n        } else {\n            return implode(", ", $stmt->errorInfo());\n        }\n    } catch (PDOException $e) {\n        return "Error: " . $e->getMessage();\n    }\n}