class TestClass {\n    private $varA;\n    private $varB;\n\n    // Setter for varA\n    public function setVarA($value) {\n        if (is_string($value)) { // Example validation\n            $this->varA = $value;\n        } else {\n            throw new InvalidArgumentException('Value must be a string');\n        }\n    }\n\n    // Getter for varA\n    public function getVarA() {\n        return $this->varA;\n    }\n\n    // Setter for varB\n    public function setVarB($value) {\n        $this->varB = $value;\n    }\n\n    // Getter for varB\n    public function getVarB() {\n        return $this->varB;\n    }\n}\n\n$x = new TestClass();\n$x->setVarA("test");\necho $x->getVarB(); // Assuming varB is set somewhere