<?php\n// Get the current URL and script path\n$selfUrl = "http://" . $_SERVER['HTTP_HOST'] . dirname($_SERVER['PHP_SELF']);\n\n// Parse the query string into an associative array\n$queryParams = array();\nif (!empty($_SERVER['QUERY_STRING'])) {\n    parse_str($_SERVER['QUERY_STRING'], $queryParams);\n}\n\n// Modify the page parameter in the query string\n$pageNumber = isset($queryParams['page']) ? (int)$queryParams['page'] : 1;\n$newPageNumberNext = $pageNumber + 1;\n$newPageNumberPrev = max(1, $pageNumber - 1);\n\n// Update the page number for next and previous links\n$queryParams['page'] = $newPageNumberNext;\n$nextUrl = $selfUrl . "?" . http_build_query($queryParams);\n\n$queryParams['page'] = $newPageNumberPrev;\n$prevUrl = $selfUrl . "?" . http_build_query($queryParams);\n?>\n\n<!-- Links -->\n<a href="<?php echo htmlspecialchars($prevUrl); ?>" class="bluelinkbold">Previous</a>\n<a href="<?php echo htmlspecialchars($nextUrl); ?>" class="bluelinkbold">Next</a>