<?php\nfunction stripAllMetadata($inputFile, $outputFile) {\n    try {\n        $image = new Imagick(realpath($inputFile));\n        \n        // Remove all profiles (metadata)\n        foreach ($image as $frame) {\n            $profiles = $frame->getImageProfiles('*', false);\n            foreach ($profiles as $name => $profile) {\n                if (!$profile['blob']) continue;\n                $frame->removeImageProfile($name);\n            }\n        }\n\n        $image->writeImages(realpath($outputFile), true);\n        \n        echo "Metadata removed from $inputFile and saved to $outputFile.\n";\n    } catch (Exception $e) {\n        echo 'Error: ', $e->getMessage(), "\n";\n    }\n}\n\n// Example usage:\nstripAllMetadata('input.png', 'output.png');\n?>