// In your LoginAction method\n$auth  = Zend_Auth::getInstance();\n$authAdapter = $this->_getAuthAdapter($formData['userName'], $formData['password']);\n$result = $auth->authenticate($authAdapter);\n\nif (!$result->isValid()) {\n    // Add error to the form (global error)\n    $form->addError('Login/password incorrect');\n\n    // Alternatively, you can add an element-specific error\n    // For example, adding it to the username field:\n    //$form->getElement('userName')->addError('Login/password incorrect');\n\n    $form->populate($formData);\n    $this->view->form = $form;\n}