<?php\nfunction sanitizeHost($host) {\n    // Validate against a regex pattern suitable for hostnames and IPs\n    if (!filter_var($host, FILTER_VALIDATE_DOMAIN, FILTER_FLAG_HOSTNAME)\n        && !filter_var($host, FILTER_VALIDATE_IP)) {\n        throw new InvalidArgumentException("Invalid hostname or IP address.");\n    }\n    \n    return escapeshellarg($host);\n}\n\n$host_to_ping = "example.com";\ntry {\n    $safe_host = sanitizeHost($host_to_ping);\n    system("ping -c 4 $safe_host", $return_var);\n} catch (InvalidArgumentException $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>