<?php\n\nfunction getArrayDepth(array $array) {\n    $maxDepth = 1;\n\n    foreach ($array as $value) {\n        if (is_array($value)) {\n            $depth = getArrayDepth($value);\n            if ($depth + 1 > $maxDepth) {\n                $maxDepth = $depth + 1;\n            }\n        }\n    }\n\n    return $maxDepth;\n}\n\n// Example usage:\n$array1 = [1, 2, 3];\n$array2 = [1, [2, 3], 4];\n$array3 = [1, [2, [3, 4]], 5];\n\necho getArrayDepth($array1); // Output: 1\necho getArrayDepth($array2); // Output: 2\necho getArrayDepth($array3); // Output: 3\n\n?>