// Define the absolute path to the target directory on the http side\n$targetDirectory = '/path/to/your/httpdocs/masonic_images/';\n\n// Ensure the uploaded file is valid and move it to the target directory\nif (isset($_FILES['blue_image']) && $_FILES['blue_image']['error'] == UPLOAD_ERR_OK) {\n    $newName = basename($_FILES['blue_image']['name']);\n    // Use absolute path for move_uploaded_file()\n    if (move_uploaded_file($_FILES['blue_image']['tmp_name'], $targetDirectory . $newName)) {\n        echo "File successfully uploaded to: " . $targetDirectory . $newName;\n    } else {\n        echo "Error moving the file.";\n    }\n} else {\n    echo "Invalid file upload.";\n}