<?php\n// Example PHP backend that aggregates data\n$response = [\n    'section1' => getDataForSection1(),\n    'section2' => getDataForSection2(),\n    'section3' => getDataForSection3()\n];\n\necho json_encode($response);\n\nfunction getDataForSection1() {\n    // Logic to fetch or process data for section 1\n    return ['dataKey1' => 'value1', 'dataKey2' => 'value2'];\n}\n\nfunction getDataForSection2() {\n    // Logic to fetch or process data for section 2\n    return ['dataKey3' => 'value3', 'dataKey4' => 'value4'];\n}\n\nfunction getDataForSection3() {\n    // Logic to fetch or process data for section 3\n    return ['dataKey5' => 'value5', 'dataKey6' => 'value6'];\n}\n?>