$text = "February 2009\nbla bla\nMarch 2009\ndoo daa bla lbla\nSeptemer 2009";\n\n// Regex pattern to match the month and year\n$regex = '/([A-Za-z]+\s\d{4})/';\n\n// Using preg_match_all to find all matches of the months and years\npreg_match_all($regex, $text, $matches);\n\n// Extracted dates\n$dates = $matches[0];\n\n// Remove the matched dates from the text to get the remaining parts\n$parts = preg_split($regex, $text, -1, PREG_SPLIT_NO_EMPTY | PREG_SPLIT_DELIM_CAPTURE);\n\necho "Dates found:\n";\nforeach ($dates as $date) {\n    echo "$date\n";\n}\n\necho "\nParts between dates:\n";\nforeach (array_slice($parts, 1) as $index => $part) {\n    if ($index % 2 != 0) { // Skip the captured delimiters\n        echo trim($part) . "\n";\n    }\n}