<?php\n\nfunction _($string) {\n    static $translations;\n    \n    if ($translations === null) {\n        // Load the configuration based on a language parameter\n        // For simplicity, assume 'english' language is default\n        $language = isset($_GET['lang']) ? $_GET['lang'] : 'english';\n        $file = dirname(__FILE__) . "/$language.ini";\n        \n        if (file_exists($file)) {\n            $translations = parse_ini_file($file);\n        } else {\n            $translations = [];\n        }\n    }\n\n    return isset($translations[$string]) ? $translations[$string] : $string;\n}\n\n// Usage\necho _("Hello, world!"); // Outputs the translated string according to the current language file\n\n?>