// Page 1: Create and save the object\n\nsession_start(); // Start or resume session\n\nclass MyObject {\n    public $property;\n}\n\n$obj = new MyObject();\n$obj->property = 'Some value';\n\n$_SESSION['my_object'] = serialize($obj); // Save serialized object in session\n\n\n\n// Page 2: Retrieve and use the object\n\nsession_start(); // Resume session\n\nif (isset($_SESSION['my_object'])) {\n    $obj = unserialize($_SESSION['my_object']); // Unserialize object from session\n    echo $obj->property; // Access object property\n}