<?php\n\n// Your XML string\n$xmlString = <<<XML\n<car>\n    <tire>Michelin</tire>\n    <seats>Leather</seats>\n    <type>\n        <color>red</color>\n        <make>Audi</make>\n    </type>\n</car>\nXML;\n\n// Create a DOMDocument object and load the XML\n$dom = new DOMDocument();\n$dom->loadXML($xmlString);\n\n// Get the <type> node\n$typeNode = $dom->getElementsByTagName('type')->item(0);\n\n// Use saveXML() to get the outer XML of the <type> node\n$outerXml = $dom->saveXML($typeNode);\n\necho htmlspecialchars($outerXml, ENT_XML1);\n?>