<?php\n// Sample array\n$foo = ['Alice', 'Bob', 'Charlie'];\n\n// Convert PHP array to a string for SQL query\n$fooString = "'" . implode("','", $foo) . "'";\n\n// Prepare and execute the query\n$query = "SELECT name FROM people WHERE name NOT IN ($fooString)";\n$result = mysqli_query($connection, $query);\n\n$missingNames = [];\nif ($result) {\n    while ($row = mysqli_fetch_assoc($result)) {\n        $missingNames[] = $row['name'];\n    }\n}\n\n// Output the names from $foo that are not in 'people'\necho "Strings not in database: ";\nprint_r(array_diff($foo, $missingNames));\n?>