<?php\n// Convert PHP array to placeholders\n$placeholders = implode(',', array_fill(0, count($foo), '?'));\n$query = "SELECT name FROM people WHERE name IN ($placeholders)"; // Adjusted to check what exists\n\n$stmt = mysqli_prepare($connection, $query);\nif (!$stmt) {\n    die("Error in prepare: " . mysqli_error($connection));\n}\n\n// Bind parameters\n$types = str_repeat('s', count($foo));\nmysqli_stmt_bind_param($stmt, $types, ...$foo);\n\nmysqli_stmt_execute($stmt);\n$result = mysqli_stmt_get_result($stmt);\n\n$existingNames = [];\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $existingNames[] = $row['name'];\n}\n\n// Close statement\nmysqli_stmt_close($stmt);\n\n// Find names in $foo that are not in the database\n$missingNames = array_diff($foo, $existingNames);\necho "Strings not in database: ";\nprint_r($missingNames);\n?>