function add_filter($hookName, $callback) {\n    static $hooks = [];\n    if (!isset($hooks[$hookName])) {\n        $hooks[$hookName] = [];\n    }\n    $hooks[$hookName][] = $callback;\n}\n\nfunction apply_filters($hookName, $value) {\n    static $hooks = [];\n    if (isset($hooks[$hookName])) {\n        foreach ($hooks[$hookName] as $callback) {\n            $value = call_user_func($callback, $value);\n        }\n    }\n\n    return $value;\n}