<?php\n$css = $_POST['css'];\n$shopName = strip_tags($_POST['title']);\n$email = filter_var($_POST['email'], FILTER_SANITIZE_EMAIL);\n\n// Create connection using mysqli\n$con = new mysqli("***", "***", "***", "***");\n\nif ($con->connect_error) {\n    die('Connection failed: ' . $con->connect_error);\n}\n\n// Prepare and bind\n$stmt = $con->prepare("INSERT INTO wps_Shops (shopName, shopEmail, shopStyle) VALUES (?, ?, ?)");\n$stmt->bind_param("sss", $shopName, $email, $css);\n\n// Execute the statement\nif ($stmt->execute()) {\n    echo "<h1>Shop created</h1>";\n    \n    // Get the last inserted ID\n    $id = $con->insert_id;\n    \n    echo("<p><a href=\"shop.php?id=$id\">Go to shop</a></p>");\n    echo("<p><a href=\"addproducts.php?id=$id\">Add products</a></p>");\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close the statement and connection\n$stmt->close();\n$con->close();\n?>