/**\n * Implements theme_button().\n */\nfunction mytheme_theme_button(array $variables) {\n  $element = $variables['element'];\n  $element ['#attributes']['type'] = 'submit';\n\n  // Ensure that the form #action attribute is properly set.\n  element_set_attributes($element, array('id'));\n\n  // Turn off the button's title. The button should have already been set up with\n  // a label before being passed to this function; therefore we don't need it in\n  // the $title attribute of the <button>.\n  unset($element['#attributes']['title']);\n\n  // Drupal core original: return '<input' . drupal_attributes($element ['#attributes']) . ' />';\n  \n  // Custom code to wrap button value inside a span element.\n  return '<button' . drupal_attributes($element ['#attributes']) . '>' .\n         '<span>' . check_plain($element ['#value']) . '</span>' .\n         '</button>';\n}