<?php\n// Database connection using PDO\n$host = 'localhost';\n$db   = 'your_database_name';\n$user = 'your_username';\n$pass = 'your_password';\n$charset = 'utf8mb4';\n\n$dsn = "mysql:host=$host;dbname=$db;charset=$charset";\n$options = [\n    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES   => false,\n];\ntry {\n     $pdo = new PDO($dsn, $user, $pass, $options);\n} catch (\PDOException $e) {\n     throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n// Fetch all data\n$stmt = $pdo->query('SELECT id, name FROM your_table_name');\n$items = $stmt->fetchAll();\n?>\n\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <title>Display Data</title>\n</head>\n<body>\n\n<h1>List of Items</h1>\n<ul>\n<?php foreach ($items as $item): ?>\n    <li><a href="?id=<?= htmlspecialchars($item['id']) ?>"><?= htmlspecialchars($item['name']) ?></a></li>\n<?php endforeach; ?>\n</ul>\n\n<!-- Display detailed information -->\n<?php if (isset($_GET['id'])):\n    $stmt = $pdo->prepare('SELECT * FROM your_table_name WHERE id = ?');\n    $stmt->execute([$pdo->quote($_GET['id'])]);\n    $detail = $stmt->fetch(); ?>\n\n<h2>Details of <?= htmlspecialchars($detail['name']) ?></h2>\n<p><?= htmlspecialchars($detail['description']) ?></p>\n<img src="images/<?= htmlspecialchars($detail['image_path']) ?>" alt="<?= htmlspecialchars($detail['name']) ?>">\n<?php endif; ?>\n\n</body>\n</html>