class UserRepository {\n    private $db;\n\n    public function __construct($db) {\n        $this->db = $db;\n    }\n\n    public function verifyLogin($email, $password) {\n        $encryptedPassword = $this->__encrypt($password);\n        return $this->doVerifyLoginQuery($email, $encryptedPassword);\n    }\n\n    private function doVerifyLoginQuery($email, $encryptedPassword) {\n        $query = "SELECT uid FROM Users WHERE email=? AND pw=?";\n        $result = $this->db->q($query, array($email, $encryptedPassword));\n\n        if (count($result) == 1) {\n            $row = $result->fetchRow(MDB2_FETCHMODE_ASSOC);\n            return $row['uid'];\n        } else {\n            return false;\n        }\n    }\n\n    // Add other user-related queries here\n}