class MyORM {\n    private $pdo; // PDO object\n    private $results; // Query results\n\n    public function __construct($pdo, $query) {\n        $this->pdo = $pdo;\n        $this->results = $pdo->query($query)->fetchAll();\n    }\n\n    public function __sleep() {\n        // Return an array of the properties you want to serialize\n        return ['results']; // Exclude 'pdo' from serialization\n    }\n\n    public function __wakeup() {\n        // Initialize or reinitialize any transient states after unserialization, if necessary\n        $this->pdo = null; // PDO will be reinitialized when needed\n    }\n}