<?php\nif(isset($_POST["id_tuto"])) {\n    $id = filter_input(INPUT_POST, 'id_tuto', FILTER_SANITIZE_STRING);\n    PrintHtml($id);\n}\n\nfunction PrintHtml($id) {\n    // Validate the ID or map it to known valid values\n    $valid_ids = ["1", "2", "3"]; // Example valid IDs\n    if (in_array($id, $valid_ids)) {\n        $filePath = 'tutorials/step' . htmlspecialchars($id) . '.html';\n        \n        if(file_exists($filePath)) {\n            readfile($filePath);\n        } else {\n            echo "Invalid tutorial ID.";\n        }\n    } else {\n        echo "Invalid request.";\n    }\n}\n?>