// Corrected regex for matching package number starting with 2,3,4 or 5 then followed by 5 digits.\nif (!preg_match('/^[2-5]\d{5}$/', $_POST['packageNumber'])) {\n    echo "The package number is not the correct format.";\n}\n\n// Corrected regex for matching package modifier as described.\nif (!preg_match('/^[2-5]\d{5}\.[1-2]$/', $_POST['packageModifier'])) {\n    echo "The package modifier is not the correct format.";\n}\n\n// Corrected regex for matching customer number with alphanumerics, spaces, periods and hyphens up to 50 characters.\nif (!preg_match('/^[0-9a-zA-Z\s\-\.]{1,50}$/', $_POST['customerNumber'])) {\n    echo "The customer number is not the correct format.";\n}