<?php\ntry {\n    // Create a new PDO instance\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db;charset=utf8', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Parameterized SELECT query to fetch ID based on name\n    $name = "JohnDoe"; // Example parameter\n    $stmtSelect = $pdo->prepare("SELECT ID FROM your_table WHERE name = :name");\n    $stmtSelect->bindParam(':name', $name, PDO::PARAM_STR);\n    $stmtSelect->execute();\n\n    // Fetch the single result\n    $row = $stmtSelect->fetch(PDO::FETCH_ASSOC);\n\n    if ($row && isset($row['ID'])) {\n        $id = $row['ID'];\n\n        // Use the fetched ID in an INSERT statement into another table\n        $dataToInsert = "Some Data"; // Example data to insert\n        $stmtInsert = $pdo->prepare("INSERT INTO another_table (foreign_id, data) VALUES (:id, :data)");\n        $stmtInsert->bindParam(':id', $id, PDO::PARAM_INT);\n        $stmtInsert->bindParam(':data', $dataToInsert, PDO::PARAM_STR);\n\n        // Execute the INSERT statement\n        if ($stmtInsert->execute()) {\n            echo "Insert operation was successful.";\n        } else {\n            echo "Failed to perform insert operation.";\n        }\n    } else {\n        echo "No record found with the given name.";\n    }\n\n} catch (PDOException $e) {\n    echo "Database error: " . $e->getMessage();\n}\n?>