class My_Form_Decorator_HiddenDdDecorator extends Zend_Form_Decorator_Abstract\n{\n    public function render($content)\n    {\n        $element = $this->getElement();\n        if ($element instanceof Zend_Form_Element_Hidden) {\n            // Modify the content to add classes\n            $pattern = '%s<dt class="hidden">&nbsp;</dt><dd class="hidden">%s</dd>%s';\n        } else {\n            // Keep the original content structure\n            $pattern = '%s<dt>&nbsp;</dt><dd>%s</dd>%s';\n        }\n        \n        return sprintf($pattern, \n                       $this->getMarkupPlacement() == self::PREPEND ? "<dl>" : "",\n                       $content,\n                       $this->getMarkupPlacement() == self::APPEND ? "</dl>" : "");\n    }\n}