<?php\n\nfunction convertToUserTimezone($timezoneOffset, $timestamp) {\n    // Convert the offset to minutes\n    $offsetMinutes = intval($timezoneOffset) * 60;\n\n    // Get the timestamp in UTC\n    $utcDateTime = new DateTime("@$timestamp", new DateTimeZone('UTC'));\n\n    // Set the timezone based on user offset\n    $userTimezone = new DateTimeZone("Etc/GMT" . ($offsetMinutes < 0 ? "+" : "-") . abs($offsetMinutes) / 60);\n    $utcDateTime->setTimeZone($userTimezone);\n\n    // Format and return the date in a readable format\n    return $utcDateTime->format('jS, l Y, g:ia');\n}\n\n// Example usage:\n$timestamp = time(); // Just an example of a query result timestamp\n$user_timezone_code = "+1"; // Example user timezone offset\n\necho convertToUserTimezone($user_timezone_code, $timestamp);\n\n?>