class My_Form extends Zend_Form\n{\n    public function init()\n    {\n        // Add your elements here\n\n        foreach ($this->getElements() as $element) {\n            // Remove any default DtDdWrapper decorator\n            $element->removeDecorator('DtDdWrapper');\n\n            // Add the custom decorator\n            $element->addDecorator(new My_Form_Decorator_HiddenDdDecorator());\n        }\n\n        // If you have form-level decorators, adjust accordingly.\n    }\n}