use Zend_Search_Lucene;\nuse Zend_Search_Lucene_Analysis_Analyzer_Common_Utf8;\nuse Zend_Search_Lucene_Exception;\n\n// Set up the UTF-8 analyzer\nZend_Search_Lucene_Analysis_Analyzer::setDefault(new Zend_Search_Lucene_Analysis_Analyzer_Common_Utf8());\n\ntry {\n    $index = Zend_Search_Lucene::open('/path/to.index', false); // Open index without creating a new one\n} catch (Exception $e) {\n    die('Failed to open the index: ' . $e->getMessage());\n}\n\n$queryString = 'name:sxample~';\n$query = Zend_Search_Lucene_Search_QueryParser::parse($queryString, 'utf-8');\n\n// Log or echo parsed query for debugging\nvar_dump((string)$query);\n\ntry {\n    $hits = $index->find($query);\n    if (count($hits) === 0) {\n        echo "No results found.\n";\n    } else {\n        foreach ($hits as $hit) {\n            // Output document information here\n            print_r($hit->getDocument()->getFieldValue('name'));\n        }\n    }\n} catch (Zend_Search_Lucene_Exception $ex) {\n    echo 'Query processing failed: ' . $ex->getMessage();\n}